function toNums(seq) {
    let parts = seq.split("|"), inc = 1, sign = 1;
    if (parts.length > 1)
        inc = parseFloat(parts[1]);

    let nums = parts[0].split(">");
    if (nums.length < 2)
        return [parseFloat(nums[0])];

    let a = parseFloat(nums[0]),
        b = parseFloat(nums[1]);
    if (a > b) {
        inc = -inc;
        sign = -1;
    }

    res = [];
    for (; a * sign <= b * sign; a += inc)
        res.push(a);

    return res;
}

function removeItems(res, items) {
    let i, len = items.length;
    for (i = 0; i < len; ++i) {
        index = res.indexOf(items[i]);
        if (-1 !== index)
            res.splice(index, 1);
    }
}

function parseItems(data) {
    let names = [];
    let entities = data.split(",");
    let i, len = entities.length;
    for (i = 0; i < len; ++i) {
        let entity = entities[i];
        let parts = entity.split(";");
        if (parts.length < 2) {
            names.push(parts[0].trim());
            continue;
        }
        if (parts.length > 2)
            continue;

        let res = [];
        let name = parts[0].trim();
        let seqs = parts[1].split(/([+]|[-])/);
        let tlen = seqs.length, j;
        for (j = 0; j < tlen; j += 2) {
            let s = toNums(seqs[j]);
            if (0 == j || "+" == seqs[j - 1])
                res = res.concat(s);
            else
                removeItems(res, s);
        }
        tlen = res.length;
        for (j = 0; j < tlen; ++j)
            names.push(name + res[j]);
    }
    return names;
}
msg.payload = parseItems(msg.payload);

return msg;

