let msgs = [null, null, null, null];
let selection = flow.get("selection");
if( undefined === selection )
    selection = "";

switch( msg.payload )
{
    case "CLEAR":
        selection = ""; 
        msg.payload = selection;
        msgs[0] = msg;
        break;

    case "FIX":
        selection = "FIX; "; 
        msg.payload = selection;
        msgs[0] = msg;
        break;

    case "STORE": break;
    case "UPDATE": break;
    case "ENTER":
        msg.payload = selection;
        msgs[1] = msg;
        break;

    default:
        selection += msg.payload;
        msg.payload = selection;
        msgs[0] = msg;
        break;
}

return msgs;

