let values = [];
let W = Math.round(flow.get("brightness")*2.55);
let R = Math.round(flow.get("red")*2.55);
let G = Math.round(flow.get("green")*2.55);
let B = Math.round(flow.get("blue")*2.55);
let rgb;
if( false == isNaN(R) && false == isNaN(G) && false == isNaN(B) )
    rgb = [R,G,B];

if ( isNaN(W) && undefined === rgb )
    return;

for( let i in entities )
{
    let n = {entity_id: entities[i].id}
    if ( false == isNaN(W) )
        n.brightness = W;
